/* global redux_change, redux_opts *//*
 * Field Sorter jquery function
 * Based on
 * [SMOF - Slightly Modded Options Framework](http://aquagraphite.com/2011/09/slightly-modded-options-framework/)
 * Version 1.4.2
 */jQuery(function(){jQuery(".sorter").each(function(){var e=jQuery(this).attr("id");jQuery("#"+e).find("ul").sortable({items:"li",placeholder:"placeholder",connectWith:".sortlist_"+e,opacity:.6,update:function(){jQuery(this).find(".position").each(function(){var t=jQuery(this).parent().attr("id"),n=jQuery(this).parent().parent().attr("id");n=n.replace(e+"_","");redux_change(jQuery(this));var r=jQuery(this).parent().parent().parent().attr("id");jQuery(this).prop("name",redux_opts.opt_name+"["+r+"]["+n+"]["+t+"]")})}})})});