// Don't duplicate me!
if( !class_exists( 'Redux_Metaboxes' ) ) {
	class Redux_Metaboxes {
		public $boxes = array();
		public $sections = array();
		public $args = array();
		public $Redux_Options;
		public $options = array();
		public $options_defaults = array();

		public function __construct( $Redux_Options = array(), $boxes = array(), $args = array() ) {
			
			if ( !isset( $Redux_Options ) || !isset( $Redux_Options->args ) ) {
				return;
			}
			$args = wp_parse_args($args, $Redux_Options->args);

			$this->args = $args;
			$this->args = apply_filters('redux/metabox/'.$this->args['opt_name'].'/args',$this->args);

			$this->boxes = $boxes;
			$this->boxes = apply_filters('redux/metabox/'.$this->args['opt_name'].'/boxes',$this->boxes);
			
			$this->Redux_Options = $Redux_Options;

			if ( empty( $this->boxes ) ) {
				return; // Don't do it! There's nothing here.
			}

			foreach( $this->boxes as $boxKey => $box ) {
				if (!empty($box['sections'])) {
					$this->sections[] = $box['sections'];
				}
			}

			add_action( 'add_meta_boxes', array( &$this, 'add_meta_boxes' ) );
			add_action( 'save_post', array( &$this, 'meta_boxes_save' ), 1, 2 );
			add_action( 'pre_post_update', array( &$this, 'pre_post_update' ) );	
			add_action( 'admin_notices', array( &$this, 'meta_boxes_show_errors' ) );	
			add_filter( 'wp', array( &$this, '_override_values' ) );	

			foreach ($this->boxes as $key => $box) {
				if ( empty( $box['sections'] ) ) {
					continue;
				}
				if ( isset( $box['post_types'] ) && !empty( $box['post_types'] ) ) {
					if ( !is_array( $box['post_types'] ) ) {
						$box['post_types'] = array( $box['post_types'] );
						$this->boxes[$boxKey]['post_types'] = $box['post_types'];
					}
					foreach( $box['post_types'] as $posttype ) {			
						add_action( 'admin_print_styles-' . $posttype, array( &$Redux_Options, '_enqueue' ) );	
					}
				}
			}
		}

		public function _override_values() {
			if( empty( $this->options_defaults ) ) {
		      	$this->_default_values(); // fill cache
		    }
			if ( $this->Redux_Options->args['global_variable'] ) {
				$meta = get_post_meta( get_post()->ID, $this->Redux_Options->args['opt_name'], true );
				if ( isset( $meta ) && !empty($meta) ) {
					$options = $this->Redux_Options->args['global_variable'];
					global $$options;
	                $data = $this->Redux_Options->options;
					foreach ( $this->options_defaults as $key=>$value ) {
						if ( isset( $meta[$key] ) ) {
							$data[$key] = $meta[$key];	
						} else {
							$data[$key] = $value;
						}
					}
					$$options = $data;			
				}
			}
		}

        public function _default_values() {
            if( !empty( $this->boxes ) && empty( $this->options_defaults ) ) {
            	foreach ($this->boxes as $key => $box) {
					if ( empty( $box['sections'] ) ) {
						continue;
					}
					// fill the cache
	                foreach( $box['sections'] as $section ) {
	                    if( isset( $section['fields'] ) ) {
	                        foreach( $section['fields'] as $field ) {
	                            if( isset( $field['default'] ) ) {
	                            	$this->options_defaults[$field['id']] = $field['default'];
	                            }
	                        }
	                    }
	                }				
				}
            }
            $this->options_defaults = apply_filters( 'redux/metabox/'.$this->args['opt_name'].'/defaults', $this->options_defaults );
        }

		public function add_meta_boxes() {
			if ( empty( $this->boxes ) || !is_array( $this->boxes ) ) {
				return;
			}
			foreach ($this->boxes as $key => $box) {
				if ( empty( $box['sections'] ) ) {
					continue;
				}
				// Save users from themselves
				if ( isset( $box['position'] ) && !in_array( strtolower( $box['position'] ), array( 'normal', 'advanced', 'side' ) ) ) {
					unset( $box['position'] );
				}
				if ( isset( $box['priority'] ) && !in_array( strtolower( $box['priority'] ), array( 'high', 'core', 'default', 'low' ) ) ) {
					unset( $box['priority'] );
				}							
				$defaults = array(
					'id' => $key.'-'.$this->Redux_Options->args['opt_name'],
					'post_types' => array('page', 'post'),
					'position' => 'normal',
					'priority' => 'high',
				);
				$box = wp_parse_args( $box, $defaults );
				if ( isset( $box['post_types'] ) && !empty( $box['post_types'] ) ) {
					foreach( $box['post_types'] as $posttype ) {
						if ( !isset($box['title'] ) ) {
							$title = ucfirst( $posttype ) . " " . __('Options', 'redux-sidebars');
						} else {
							$title = $box['title'];
						}
						$args = array(
							'position' => $box['position'],
							'sections' => $box['sections']
						);
						add_meta_box( 'redux-metabox-'.$box['id'], $title, array( &$this, 'generate_boxes' ), $posttype, $box['position'], $box['priority'], $args );	
					}	
				}
			}
		}

		function _field_default($field_id) {
        	if ( !isset( $this->Redux_Options->options_defaults ) ) {
        		$this->Redux_Options->options_defaults = $this->Redux_Options->_default_values();
        	}
			if ( isset($this->Redux_Options->options[$field_id] ) && $this->Redux_Options->options[$field_id] != $this->Redux_Options->options_defaults[$field_id] ) {
				return $this->Redux_Options->options[$field_id];
			} else {
				if( empty( $this->options_defaults ) ) {
		        	$this->_default_values(); // fill cache
		        }
		        if ( !empty( $this->options_defaults ) ) {
		        	$data = isset( $this->options_defaults[$field_id] ) ? $this->options_defaults[$field_id] : '';	
		        }	
				if ( empty( $data ) && isset( $this->Redux_Options->options_defaults[$field_id] ) ) {
		    		$data = $this->Redux_Options->options_defaults[$field_id];	
		    	}
		    	return $data;						                										
			}			
		}

